// src/components/DeletePage.jsx
import React, { useEffect, useState } from "react";
import styled from "styled-components";
import axios from "axios";
import { theme } from './theme'; // Import theme for usage

const DeletePage = () => {
  const [instructors, setInstructors] = useState([]);
  const [courses, setCourses] = useState([]);

  useEffect(() => {
    // Fetch instructors and courses data
    const fetchData = async () => {
      try {
        const instructorResponse = await axios.get('/api/instructors');
        const courseResponse = await axios.get('/api/courses');
        setInstructors(instructorResponse.data);
        setCourses(courseResponse.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  const handleDelete = async (type, id) => {
    try {
      if (type === "instructor") {
        await axios.delete(`/api/instructors/${id}`);
        setInstructors(instructors.filter((instructor) => instructor._id !== id));
      } else if (type === "course") {
        await axios.delete(`/api/courses/${id}`);
        setCourses(courses.filter((course) => course._id !== id));
      }
    } catch (error) {
      console.error("Error deleting item:", error);
    }
  };

  return (
    <DeleteContainer>
      <Section>
        <h2>Instructors</h2>
        <ItemList>
          {instructors.map((instructor) => (
            <Item key={instructor._id}>
              {instructor.name}
              <DeleteButton onClick={() => handleDelete("instructor", instructor._id)}>
                Delete
              </DeleteButton>
            </Item>
          ))}
        </ItemList>
      </Section>
      <Section>
        <h2>Courses</h2>
        <ItemList>
          {courses.map((course) => (
            <Item key={course._id}>
              {course.title}
              <DeleteButton onClick={() => handleDelete("course", course._id)}>
                Delete
              </DeleteButton>
            </Item>
          ))}
        </ItemList>
      </Section>
    </DeleteContainer>
  );
};

// Styled Components
const DeleteContainer = styled.div`
  width: 90%;
  max-width: 1200px;
  padding: 20px;
  background-color: ${theme.cardBackground}; /* Apply theme color */
  border-radius: 12px;
  box-shadow: 0 8px 16px rgba(0, 0, 0, 0.4);
  margin-top: 20px;
  color: ${theme.textColor}; /* Apply theme color */
`;

const Section = styled.div`
  margin-bottom: 20px;
`;

const ItemList = styled.ul`
  list-style-type: none;
  padding: 0;
`;

const Item = styled.li`
  background-color: ${theme.backgroundColor}; /* Apply theme color */
  color: ${theme.textColor}; /* Apply theme color */
  padding: 10px;
  border-radius: 8px;
  margin-bottom: 10px;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const DeleteButton = styled.button`
  background-color: #e74c3c; /* Red color for delete */
  color: white;
  padding: 5px 10px;
  border: none;
  border-radius: 5px;
  cursor: pointer;

  &:hover {
    background-color: #c0392b; /* Darker red for hover */
  }
`;

export default DeletePage;
