import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import Welcome from "./Welcome";
import styled from "styled-components";

export default function Admin() {
  const [user, setUser] = useState(undefined);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        if (!localStorage.getItem("secret-key-admin")) {
          navigate("/login");
        } else {
          const storedUser = JSON.parse(localStorage.getItem("secret-key-admin"));
          setUser(storedUser);
          // console.log("user hai bhai ", storedUser);
        }
      } catch (error) {
        console.error("Error fetching user data", error);
      }
    };
    fetchData();
  }, [navigate]);

  return (
    <Container>
      <Sidebar>
        <h1>Admin Panel</h1>
        {/* Add sidebar links or other elements here */}
      </Sidebar>
      <Content>
        <Welcome user={user} />
      </Content>
    </Container>
  );
}

const Container = styled.div`
  height: 100vh;
  width: 100vw;
  background-color: #121212; /* Dark background color for the whole page */
  display: flex;
`;

const Sidebar = styled.div`
  width: 25%;
  background-color: #1f1f1f; /* Slightly lighter dark background for the sidebar */
  padding: 2rem;
  color: #e0e0e0; /* Light text color */
  display: flex;
  flex-direction: column;
  align-items: center;
  border-right: 1px solid #333; /* Dark border between sidebar and content */

  h1 {
    color: #bb86fc; /* Light purple for the sidebar header */
    font-size: 1.5rem;
    margin-bottom: 2rem;
  }

  /* Style for sidebar links or other elements */
  a {
    color: #e0e0e0; /* Light text color for links */
    text-decoration: none;
    font-size: 1.1rem;
    margin: 0.5rem 0;
    transition: color 0.3s ease;

    &:hover {
      color: #bb86fc; /* Light purple on hover */
    }
  }
`;

const Content = styled.div`
  width: 75%;
  background-color: #00000076; /* Dark background with slight opacity */
  color: #e0e0e0; /* Light text color */
  padding: 2rem;
  overflow-y: auto; /* Scroll if content overflows */
`;
