import React, { useState, useEffect } from "react";
import axios from "axios";
import styled from "styled-components";
import { allInstructors, getCourse } from "../../utils/APIRoutes";
import { theme } from './theme';

const DeletePage = () => {
  const [instructors, setInstructors] = useState([]);
  const [courses, setCourses] = useState([]);

  useEffect(() => {
    fetchInstructors();
    fetchCourses();
  }, []);

  const fetchInstructors = async () => {
    try {
      const response = await axios.get(allInstructors);
      setInstructors(response.data);
    } catch (error) {
      console.error("Error fetching instructors:", error);
    }
  };

  const fetchCourses = async () => {
    try {
      const response = await axios.get(getCourse);
      setCourses(response.data);
    } catch (error) {
      console.error("Error fetching courses:", error);
    }
  };

  const handleDeleteInstructor = async (id) => {
    try {
      await axios.delete(`/api/auth/deleteinstructor/${id}`);
      setInstructors(instructors.filter(instructor => instructor._id !== id));
    } catch (error) {
      console.error("Error deleting instructor:", error);
    }
  };

  const handleDeleteCourse = async (id) => {
    try {
      await axios.delete(`/api/auth/deletecourse/${id}`);
      setCourses(courses.filter(course => course._id !== id));
    } catch (error) {
      console.error("Error deleting course:", error);
    }
  };

  return (
    <DeletePageContainer>
      <Section>
        <h2>Instructors</h2>
        {instructors.map(instructor => (
          <Item key={instructor._id}>
            <span>{instructor.name}</span>
            <DeleteButton onClick={() => handleDeleteInstructor(instructor._id)}>
              Delete Instructor
            </DeleteButton>
          </Item>
        ))}
      </Section>
      <Section>
        <h2>Courses</h2>
        {courses.map(course => (
          <Item key={course._id}>
            <span>{course.name}</span>
            <DeleteButton onClick={() => handleDeleteCourse(course._id)}>
              Delete Course
            </DeleteButton>
          </Item>
        ))}
      </Section>
    </DeletePageContainer>
  );
};

// Styled Components

const DeletePageContainer = styled.div`
  width: 90%;
  max-width: 1200px;
  margin: 0 auto;
  padding: 20px;
  background-color: ${theme.cardBackground};
  border-radius: 12px;
  box-shadow: 0 8px 16px rgba(0, 0, 0, 0.4);
  color: ${theme.textColor};
`;

const Section = styled.div`
  margin-bottom: 20px;
`;

const Item = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 10px;
  margin-bottom: 10px;
  background-color: ${theme.backgroundColor};
  border-radius: 8px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
`;

const DeleteButton = styled.button`
  background-color: ${theme.buttonColor};
  color: ${theme.textColor};
  border: none;
  padding: 5px 10px;
  border-radius: 5px;
  cursor: pointer;

  &:hover {
    background-color: ${theme.buttonHoverColor};
  }
`;

export default DeletePage;
