import React, { useEffect } from "react";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";
import Instructors from "./Instructors";
import Courses from "./CoursesAdmin";
import Logout from "../Logout";
import { theme } from './theme'; // Import the theme for usage

export default function Welcome({ user }) {
  const navigate = useNavigate();

  useEffect(() => {
    if (localStorage.getItem("secret-key-admin")) {
      navigate("/admin");
    } else if (localStorage.getItem("secret-key")) {
      navigate("/instructor");
    } else {
      navigate("/");
    }
  }, [navigate]);

  return (
    <WelcomeContainer>
      <TopBar>
        <Logout />
        <Heading>Welcome Admin</Heading>
      </TopBar>
      <MainContent>
        <InstructorsContainer>
          <Instructors user={user} />
        </InstructorsContainer>
        <CoursesContainer>
          <Courses user={user} />
        </CoursesContainer>
      </MainContent>
    </WelcomeContainer>
  );
}

const WelcomeContainer = styled.div`
  height: 100vh;
  width: 100vw;
  background-color: ${theme.backgroundColor}; /* Apply theme color */
  display: flex;
  flex-direction: column;
  align-items: center;
  color: ${theme.textColor}; /* Apply theme color */
`;

const MainContent = styled.div`
  flex: 1;
  display: flex;
  justify-content: space-between;
  gap: 1rem;
  min-height: 100%;
  width: 100%;
  padding: 1rem;
  box-sizing: border-box;
`;

const InstructorsContainer = styled.div`
  width: 40%;
  background-color: ${theme.cardBackground}; /* Apply theme color */
  color: ${theme.textColor}; /* Apply theme color */
  padding: 1rem;
  border-radius: 10px; /* Updated border radius for a modern look */
  box-shadow: 0 6px 12px rgba(0, 0, 0, 0.4); /* Updated shadow for better contrast */
  overflow-y: auto; /* Allow scrolling if content overflows */

  h2 {
    font-size: 22px; /* Slightly larger heading for better emphasis */
    margin-bottom: 1rem;
    color: ${theme.primaryColor}; /* Apply theme color */
  }

  ul {
    list-style: none;
    padding: 0;
    margin: 0;

    li {
      font-size: 16px;
      margin-bottom: 10px;
      border-bottom: 1px solid ${theme.borderColor}; /* Apply theme color */
      padding-bottom: 5px;
      transition: color 0.3s ease-in-out;

      &:hover {
        color: ${theme.secondaryColor}; /* Highlight color on hover */
      }
    }
  }
`;

const CoursesContainer = styled.div`
  width: 60%;
  background-color: ${theme.cardBackground}; /* Apply theme color */
  color: ${theme.textColor}; /* Apply theme color */
  padding: 1rem;
  border-radius: 10px; /* Updated border radius for a modern look */
  box-shadow: 0 6px 12px rgba(0, 0, 0, 0.4); /* Updated shadow for better contrast */
  overflow-y: auto; /* Allow scrolling if content overflows */

  h2 {
    font-size: 22px; /* Slightly larger heading for better emphasis */
    margin-bottom: 1rem;
    color: ${theme.primaryColor}; /* Apply theme color */
  }

  .course-list {
    display: flex;
    flex-direction: column;
    gap: 1rem;

    .course-item {
      background-color: ${theme.backgroundColor}; /* Apply theme color */
      border: 1px solid ${theme.borderColor}; /* Apply theme color */
      border-radius: 8px; /* Updated border radius for a modern look */
      padding: 1rem;
      box-shadow: 0 4px 8px rgba(0, 0, 0, 0.3); /* Updated shadow for better contrast */

      h3 {
        font-size: 18px;
        margin: 0;
        color: ${theme.primaryColor}; /* Apply theme color */
      }

      p {
        margin: 0.5rem 0;
        font-size: 14px;
      }
    }
  }
`;

const TopBar = styled.div`
  width: 100%;
  background: ${theme.backgroundColor}; /* Apply theme color */
  padding: 10px 20px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  border-bottom: 1px solid ${theme.borderColor}; /* Apply theme color */
`;

const Heading = styled.h1`
  color: ${theme.accentColor}; /* Apply theme color */
  font-size: 24px; /* Slightly smaller font size for a modern look */
  font-weight: 500; /* Medium weight for a modern touch */
  margin: 0;
`;
