// src/components/DeleteComponent.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";
import styled from "styled-components";
import { theme } from './theme'; // Import theme for consistency

const DeletePage = () => {
  const [instructors, setInstructors] = useState([]);
  const [courses, setCourses] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [instructorsRes, coursesRes] = await Promise.all([
          axios.get('/api/instructors'),
          axios.get('/api/courses'),
        ]);

        setInstructors(instructorsRes.data);
        setCourses(coursesRes.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  const handleDelete = async (type, id) => {
    try {
      await axios.delete(`/api/${type}/${id}`);
      if (type === "instructors") {
        setInstructors(prev => prev.filter(inst => inst._id !== id));
      } else if (type === "courses") {
        setCourses(prev => prev.filter(course => course._id !== id));
      }
    } catch (error) {
      console.error("Error deleting item:", error);
    }
  };

  return (
    <DeleteContainer>
      <Section>
        <h2>Instructors</h2>
        {instructors.map(inst => (
          <Item key={inst._id}>
            <p>{inst.name}</p>
            <DeleteButton onClick={() => handleDelete('instructors', inst._id)}>Delete</DeleteButton>
          </Item>
        ))}
      </Section>
      <Section>
        <h2>Courses</h2>
        {courses.map(course => (
          <Item key={course._id}>
            <p>{course.title}</p>
            <DeleteButton onClick={() => handleDelete('courses', course._id)}>Delete</DeleteButton>
          </Item>
        ))}
      </Section>
    </DeleteContainer>
  );
};

const DeleteContainer = styled.div`
  width: 100%;
  padding: 20px;
  background-color: ${theme.backgroundColor}; /* Apply theme color */
  color: ${theme.textColor}; /* Apply theme color */
`;

const Section = styled.div`
  margin-bottom: 20px;

  h2 {
    font-size: 24px;
    color: ${theme.primaryColor}; /* Apply theme color */
    margin-bottom: 10px;
  }
`;

const Item = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  background-color: ${theme.cardBackground}; /* Apply theme color */
  padding: 10px;
  border-radius: 8px;
  margin-bottom: 10px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
`;

const DeleteButton = styled.button`
  background-color: #ff4d4d; /* Red color */
  color: white;
  border: none;
  padding: 8px 16px;
  border-radius: 5px;
  cursor: pointer;

  &:hover {
    background-color: #cc0000; /* Darker red */
  }
`;

export default DeletePage;
