import React, { useEffect } from "react";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";
import Instructors from "./Instructors";
import Courses from "./CoursesAdmin";
import Logout from "../Logout";
import { theme } from './theme'; // Import the theme for usage

export default function Welcome({ user }) {
  const navigate = useNavigate();

  useEffect(() => {
    if (localStorage.getItem("secret-key-admin")) {
      navigate("/admin");
    } else if (localStorage.getItem("secret-key")) {
      navigate("/instructor");
    } else {
      navigate("/");
    }
  }, [navigate]);

  return (
    <WelcomeContainer>
      <TopBar>
        <Logout />
        <Heading>Welcome Admin</Heading>
      </TopBar>
      <MainContent>
        <ContentSection>
          <InstructorsContainer>
            <Instructors user={user} />
          </InstructorsContainer>
          <CoursesContainer>
            <Courses user={user} />
          </CoursesContainer>
        </ContentSection>
      </MainContent>
    </WelcomeContainer>
  );
}

const WelcomeContainer = styled.div`
  height: 100vh;
  width: 100vw;
  background-color: ${theme.backgroundColor}; /* Apply theme color */
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const MainContent = styled.div`
  flex: 1;
  display: flex;
  justify-content: center;
  align-items: flex-start;
  width: 100%;
  padding: 20px;
`;

const ContentSection = styled.div`
  display: flex;
  flex-direction: column;
  gap: 20px;
  width: 100%;
  max-width: 1200px;
`;

const InstructorsContainer = styled.div`
  width: 100%;
  background-color: ${theme.cardBackground}; /* Apply theme color */
  color: ${theme.textColor}; /* Apply theme color */
  padding: 1rem;
  border-radius: 10px;
`;

const CoursesContainer = styled.div`
  width: 100%;
  background-color: ${theme.cardBackground}; /* Apply theme color */
  color: ${theme.textColor}; /* Apply theme color */
  padding: 1rem;
  border-radius: 10px;
`;

const TopBar = styled.div`
  width: 100%;
  background: ${theme.cardBackground}; /* Apply theme color */
  padding: 20px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  border-bottom: 2px solid ${theme.borderColor}; /* Dark border */
`;

const Heading = styled.h1`
  color: ${theme.primaryColor}; /* Light purple */
  font-size: 28px;
  font-weight: bold;
  margin: 0;
`;
